{
   "conversations":[
      {{#repeat 10}}
      {
         "id":"{{ guid }}",
         "attendants":[
            {
               "id":"{{ urlParam 'uid' }}",
               "name":"Francisco Souza"
            },
            {
               "id": "{{ guid }}",
               "name": "{{ firstName }} {{ lastName }}",
               "status": "{{ oneOf (array 'online' 'offline' 'busy' 'away') }}",
               "music": "My Boy band of Lucas Oliveira (Apple Music)",
               "imageUrl": "https://picsum.photos/id/{{ int 100 900 }}/200"
            }
         ],
         "messages":[
            {
               "id":"m01",
               "from":"{{ urlParam 'uid' }}",
               "to":"u02",
               "content":"Some text content here heheheh"
            },
            {
               "id":"m02",
               "from":"u02",
               "to":"{{ urlParam 'uid' }}",
               "content":"Some text content here 2 heheheh"
            },
            {
               "id":"m03",
