      {{/ repeat}}
   ]
}

{
    "calls": [
        {{#repeat 10}}
            {
               "id": "{{ guid }}",
               "startedAt": "{{now 'YYYY-DD-MM HH:mm'}} ",
               "endedAt": "{{now 'YYYY-DD-MM HH:mm'}}",
               "attendants": [
                   {
                       "id": "{{ urlParam 'uid' }}",
                       "name": "Francis Souza"
                   },
                   {
                       "id": "{{ guid }}",
                       "name": "{{ firstName }} {{ lastName }}",
                       "status": "online",
                       "music": "My Boy band of Lucas Oliveira (Apple Music)",
                       "imageUrl": "https://picsum.photos/id/{{ int 100 900 }}/200"
                   }
                ]
            }
        {{/ repeat}}
    ]
}

